from web3.auto import w3
import json
import ruamel.yaml as yaml
import sys

w3.eth.account.enable_unaudited_hdwallet_features()

testnet_config_path = "genesis-config.yaml"
mainnet_config_path = "/apps/el-gen/mainnet/genesis.json"
