goerli_config_path = "/apps/el-gen/goerli/genesis.json"
holesky_config_path = "/apps/el-gen/holesky/genesis.json"

if len(sys.argv) > 1:
    testnet_config_path = sys.argv[1]

with open(testnet_config_path) as stream:
    data = yaml.safe_load(stream)

